/*--------------------------------------------------------------
>> TABLE OF CONTENTS:
----------------------------------------------------------------
1. Typography
2. Preloader
4. General
4. Slider
5. Video Popup
6. Header
7. Footer
8. Isotope
9. Experience
10. Service
11. Hero
12. Iconbox
13. Posts
14. Team
15. Testimonial
16. Portfolio
17. Pricing
18. Steps
19. Video Modal
--------------------------------------------------------------*/
/*--------------------------------------------------------------
## Basic Color
----------------------------------------------------------------*/
/*--------------------------------------------------------------
1. Typography
----------------------------------------------------------------*/
body,
html {
  color: #666;
  font-family: "Rubik", sans-serif;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.8em;
  overflow-x: hidden;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  clear: both;
  color: #18191d;
  padding: 0;
  margin: 0 0 20px 0;
  font-weight: 600;
  line-height: 1.2em;
}

h1 {
  font-size: 56px;
}

h2 {
  font-size: 42px;
}

h3 {
  font-size: 30px;
}

h4 {
  font-size: 24px;
}

h5 {
  font-size: 18px;
}

h6 {
  font-size: 16px;
}

p {
  margin-bottom: 15px;
}

ul {
  margin: 0 0 25px 0;
  padding-left: 20px;
  list-style: square outside none;
}

ol {
  padding-left: 20px;
  margin-bottom: 25px;
}

dfn,
cite,
em,
i {
  font-style: italic;
}

blockquote {
  margin: 0 15px;
  font-style: italic;
  font-size: 20px;
  line-height: 1.6em;
  margin: 0;
}

address {
  margin: 0 0 15px;
}

img {
  border: 0;
  max-width: 75%;
  height: auto;
}

a {
  color: inherit;
  text-decoration: none;
  transition: all 0.3s ease;
}

button {
  color: inherit;
  transition: all 0.3s ease;
}

a:hover {
  text-decoration: none;
  color: #e9a132;
}

table {
  width: 100%;
  margin-bottom: 25px;
}
table th {
  font-weight: 600;
  color: #666;
}
table td,
table th {
  border-top: 1px solid #eaeaea;
  padding: 11px 10px;
}

dl {
  margin-bottom: 25px;
}
dl dt {
  font-weight: 600;
}

b,
strong {
  font-weight: bold;
}

pre {
  color: #666;
  border: 1px solid #eaeaea;
  font-size: 18px;
  padding: 25px;
  border-radius: 5px;
}

kbd {
  font-size: 100%;
  background-color: #666;
  border-radius: 5px;
}

input,
textarea {
  color: #18191d;
}

/*--------------------------------------------------------------
  2. Preloader
----------------------------------------------------------------*/
.cs_preloader {
  position: fixed;
  z-index: 99999;
  top: 0;
  width: 100%;
  height: 100vh;
}

.cs_preloader_bg {
  text-align: center;
  height: 100%;
  width: 100%;
}

.cs_preloader_in {
  width: 120px;
  height: 120px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 28px;
  border: 3px solid rgba(233, 161, 50, 0.3);
  border-radius: 50%;
}

.cs_preloader_in:after {
  content: "";
  border-width: 3px;
  border-style: solid;
  border-color: transparent;
  border-top-color: #e9a132;
  border-radius: 50%;
  position: absolute;
  width: calc(100% + 6px);
  height: calc(100% + 6px);
  left: 50%;
  top: 50%;
  animation: spin 1s ease-in-out infinite;
  margin-left: -60px;
  margin-top: -60px;
}

@-webkit-keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
/*--------------------------------------------------------------
  3. General
----------------------------------------------------------------*/
.cs_gap_y_40 {
  gap: 40px 0;
}

.cs_gray_bg {
  background-color: rgba(242, 243, 247, 0.4);
}

.cs_tab {
  display: none;
}
.cs_tab.active {
  display: block;
}

/* All Button Design */
.cs_btn.cs_style_1 {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  line-height: 1.6em;
  color: #fff;
  background-color: #e9a132;
  position: relative;
  outline: none;
  border: none;
}
.cs_btn.cs_style_1 span {
  position: relative;
  z-index: 1;
}
.cs_btn.cs_style_1::before {
  content: "";
  position: absolute;
  height: 100%;
  width: 100%;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.25);
  opacity: 0;
  transition: all 0.4s ease;
}
.cs_btn.cs_style_1:hover::before {
  opacity: 1;
}
.cs_btn.cs_style_1.cs_color_1 {
  background-color: #18191d;
}

.cs_btn.cs_style_2 {
  border: 1px solid #18191d;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  line-height: 1.6em;
  color: #18191d;
  background-color: transparent;
}
.cs_btn.cs_style_2:hover {
  background-color: #e9a132;
  color: #fff;
  border-color: #e9a132;
}

/* End Button Design */
.cs_map {
  height: 500px;
}
.cs_map iframe {
  height: 100%;
  width: 100%;
  border: none;
  filter: grayscale(100%);
}

.cs_social_btns a {
  border: 1px solid #454a53;
}
.cs_social_btns a:hover {
  border-color: #e9a132;
  background-color: #e9a132;
}

.form-control {
  background: #f6f6f6;
  padding: 19px 20px;
}
.form-control:focus {
  color: #18191d;
  background-color: #f6f6f6;
  box-shadow: 0 0 0 0.25rem rgba(233, 161, 50, 0.25);
}

.form-control-2 {
  transition: all 0.3s ease;
  border: 1px solid #18191d;
  width: 100%;
  display: block;
  padding: 12px 20px;
  outline: none;
  height: 56px;
}
.form-control-2:focus {
  border-color: #e9a132;
}

label {
  margin-bottom: 12px;
}

.cs-right-full-width {
  width: calc(50vw - 12px);
  padding-right: 24px;
}

.cs-left-full-width {
  height: 100%;
  width: 60vw;
  margin-left: calc(-50vw + 100%);
}

.cs_rating {
  width: 101px;
  line-height: 16px;
  font-size: 14px;
  position: relative;
  letter-spacing: 5px;
}
.cs_rating:before {
  content: "\f005\f005\f005\f005\f005";
  font-family: "Font Awesome 6 Free";
  font-weight: 900;
  opacity: 0.3;
  line-height: inherit;
}
.cs_rating > * {
  position: absolute;
  height: 100%;
  width: 100%;
  left: 0;
  top: 0;
}
.cs_rating .cs_rating_percentage {
  line-height: inherit;
  overflow: hidden;
}
.cs_rating .cs_rating_percentage:before {
  content: "\f005\f005\f005\f005\f005";
  font-family: "Font Awesome 6 Free";
  font-weight: 900;
  line-height: inherit;
}

.cs_moving_wrap {
  background-color: #17191d;
  height: 200px;
}
.cs_moving_wrap:hover .cs_moving_text {
  animation-play-state: paused;
}
@media (max-width: 991px) {
  .cs_moving_wrap {
    height: 120px;
  }
}

.cs_moving_text {
  animation: slide-left 20s linear infinite;
}
.cs_moving_text > * {
  padding: 10px 50px;
}
@media (max-width: 991px) {
  .cs_moving_text > * {
    padding: 10px 20px;
  }
}

@keyframes slide-left {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-100%);
  }
}
@media (max-width: 991px) {
  .cs_section_heading.cs_style_1 {
    flex-direction: column;
  }
  .cs_section_heading.cs_style_1 .text-end {
    text-align: left !important;
  }
}
.cs_section_heading.cs_style_1 .cs_section_heading_in {
  flex: 1;
}
@media (max-width: 991px) {
  .cs_section_heading.cs_style_1 .cs_section_heading_in {
    width: 100%;
  }
}
.cs_section_heading.cs_style_1 .cs_section_heading_right {
  width: 40%;
}
@media (max-width: 991px) {
  .cs_section_heading.cs_style_1 .cs_section_heading_right {
    width: 100%;
  }
}
@media (max-width: 991px) {
  .cs_section_heading.cs_style_1 p br {
    display: none;
  }
}
@media (max-width: 575px) {
  .cs_section_heading.cs_style_1 br {
    display: none;
  }
}

/* Start Accordion */
.cs_faq-1-bg {
  height: calc(100% - 110px);
}

.cs_accordian_head {
  position: relative;
  cursor: pointer;
}
.cs_accordian_head:hover .cs_accordian_title {
  color: #e9a132;
}

.cs_accordian {
  border: 1px solid transparent;
}
.cs_accordian:not(:last-child) {
  margin-bottom: 10px;
}
.cs_accordian.active {
  border-color: #eaeaea;
}
.cs_accordian.bg-white.active {
  border-color: transparent;
}

.cs_accordian_toggle {
  display: flex;
  position: absolute;
  top: 50%;
  right: 25px;
  height: 10px;
  width: 10px;
  transform: translateY(-50%);
}
.cs_accordian_toggle::before, .cs_accordian_toggle::after {
  content: "";
  position: absolute;
  height: 1px;
  width: 100%;
  top: 50%;
  left: 0;
  background-color: currentColor;
  transition: all 0.3s ease;
}
.cs_accordian_toggle::after {
  transform: rotate(90deg);
}

.cs_accordian.active .cs_accordian_toggle::after {
  transform: rotate(0deg);
}
.cs_accordian.active .cs_accordian_head {
  pointer-events: none;
  background: transparent !important;
}
.cs_accordian.active .cs_accordian_title {
  color: #e9a132;
}

/* End Accordion */
.cs_contact_wrap .cs_contact_image {
  position: absolute;
  bottom: -148px;
  right: -100%;
  max-width: 450px;
  margin-right: 105px;
  pointer-events: none;
}
@media (max-width: 1550px) {
  .cs_contact_wrap .cs_contact_image {
    right: -72%;
    max-width: 390px;
  }
}
@media (max-width: 1400px) {
  .cs_contact_wrap .cs_contact_image {
    bottom: -170px;
    right: -82%;
  }
}
.cs_contact_wrap textarea {
  height: 141px;
}
.cs_contact_wrap.cs_type_1 {
  margin-top: -180px;
  position: relative;
  z-index: 2;
}
@media (max-width: 1199px) {
  .cs_contact_wrap.cs_type_1 {
    margin-top: 30px;
  }
}

.cs_contact_form {
  background-position: bottom left;
  background-repeat: no-repeat;
}
.cs_contact_form .form-control {
  resize: none;
}

/* End Contact Section */
/* Start Progress Bar */
.cs_progressbar.cs_style_1 .cs_progress {
  height: 8px;
  background-color: #f6f6f6;
}
.cs_progressbar.cs_style_1.cs_type_1 .cs_progress_in {
  position: relative;
}
.cs_progressbar.cs_style_1.cs_type_1 .cs_progress_in::after {
  content: "";
  height: 18px;
  width: 18px;
  background-color: #e9a132;
  border-radius: 50%;
  position: absolute;
  right: 0;
  margin-top: -5px;
}

/* End Progress Bar */
.cs_player_btn:before, .cs_player_btn:after {
  content: "";
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translateX(-50%) translateY(-50%);
  display: block;
  width: 100%;
  height: 100%;
  background-color: rgba(24, 25, 29, 0.4);
  border-radius: 50%;
}
.cs_player_btn:before {
  z-index: 0;
  animation: pulse-border 1500ms ease-out infinite;
}
.cs_player_btn:after {
  z-index: 1;
  transition: all 200ms;
}
.cs_player_btn svg {
  position: relative;
  z-index: 2;
}

@keyframes pulse-border {
  0% {
    transform: translateX(-50%) translateY(-50%) translateZ(0) scale(1);
    opacity: 1;
  }
  100% {
    transform: translateX(-50%) translateY(-50%) translateZ(0) scale(1.5);
    opacity: 0;
  }
}
.cs_section_shape-1 {
  left: 82%;
  top: 6%;
}

.cs_section_shape-2 {
  left: 78%;
  bottom: -60px;
}

.cs_about-1-spacing {
  padding-left: 110px;
}
@media (max-width: 1400px) {
  .cs_about-1-spacing {
    padding-left: 50px;
  }
}
@media (max-width: 1199px) {
  .cs_about-1-spacing {
    padding-left: 0px;
    padding-top: 50px;
  }
}

.cs_page_header {
  height: 640px;
  padding: 190px 0 50px;
}
.cs_page_header::before {
  content: "";
  position: absolute;
  height: 100%;
  width: 100%;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.8);
}
@media (max-width: 991px) {
  .cs_page_header {
    height: 500px;
  }
}

.breadcrumb-item + .breadcrumb-item::before {
  content: ">";
  color: #e9a132;
}

.breadcrumb-item.active {
  color: #e9a132;
}

.cs_social_btns.cs_color_1 a {
  border-color: #666;
  background-color: #666;
}
.cs_social_btns.cs_color_1 a:hover {
  background-color: #e9a132;
  border-color: #e9a132;
}

.cs_social_btns.cs_color_2 a {
  border-color: #d8d8d8;
  color: #666;
}
.cs_social_btns.cs_color_2 a:hover {
  background-color: #e9a132;
  border-color: #e9a132;
  color: #fff;
}

.cs_roadmaps .cs_roadmap {
  flex: 1;
  border-top: 1px solid #eaeaea;
  padding-right: 15px;
  margin-top: 5px;
}
.cs_roadmaps .cs_roadmap span {
  margin-top: -6px;
  height: 10px;
  width: 10px;
}
.cs_roadmaps .cs_roadmap span:after {
  content: "";
  position: absolute;
  left: 50%;
  top: 50%;
  margin-left: -5px;
  margin-top: -5px;
  height: 10px;
  width: 10px;
  background: rgba(233, 161, 50, 0.6);
  border-radius: 50%;
  box-shadow: 0 0 0 4px rgba(233, 161, 50, 0.6);
  opacity: 0;
}
.cs_roadmaps .cs_roadmap:hover span::after {
  animation-name: splateAnimation;
  animation-duration: 1.5s;
}
@media (max-width: 991px) {
  .cs_roadmaps {
    flex-wrap: wrap;
  }
  .cs_roadmaps .cs_roadmap {
    flex: none;
    width: 50%;
  }
}
@media (max-width: 575px) {
  .cs_roadmaps .cs_roadmap {
    width: 100%;
    padding-right: 0;
  }
}

@keyframes splateAnimation {
  0% {
    transform: scale(0.5);
    opacity: 1;
  }
  100% {
    transform: scale(1.7);
    opacity: 0;
  }
}
.cs_project_details-info {
  list-style: none;
  border-top: 5px solid #fec63f;
  box-shadow: 0px 4px 60px rgba(179, 179, 179, 0.1);
}
.cs_project_details-info li:not(:last-child) {
  margin-bottom: 28px;
}
.cs_project_details-info .cs_social_btns a {
  border-color: #eaeaea;
  color: #666;
}
.cs_project_details-info .cs_social_btns a:hover {
  color: #fff;
  background-color: #e9a132;
}

.cs_list.cs_style_1 {
  list-style: none;
  padding: 0;
}
.cs_list.cs_style_1 li {
  padding-left: 25px;
  position: relative;
}
.cs_list.cs_style_1 li::before {
  content: "\f00c";
  font-weight: 900;
  font-family: "Font Awesome 6 Free";
  position: absolute;
  left: 0;
  top: 0;
  color: #e9a132;
}
.cs_list.cs_style_1 li:not(:last-child) {
  margin-bottom: 10px;
}
.cs_list.cs_style_1 li span {
  font-size: 14px;
  line-height: 1.6em;
  font-weight: 500;
  padding: 2px 10px;
  display: inline-block;
  background-color: #666;
  color: #fff;
  margin-left: 10px;
  border-radius: 5px;
}

.cs_list.cs_style_2 {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  grid-gap: 18px 24px;
  list-style: none;
  padding: 0;
}
@media (max-width: 991px) {
  .cs_list.cs_style_2 {
    grid-template-columns: repeat(1, 1fr);
    grid-gap: 10px;
  }
}
.cs_list.cs_style_2 li {
  padding-left: 30px;
  position: relative;
}
.cs_list.cs_style_2 li i {
  display: flex;
  position: absolute;
  left: 0;
  top: 3px;
}

.cs_service_list ul {
  list-style: none;
  border: 1px solid rgba(112, 112, 112, 0.7);
  border-top: 0;
}
.cs_service_list a {
  background-color: #f5f3ee;
  color: #e9a132;
  display: block;
  position: relative;
  border-radius: 5px;
  padding: 15px 45px 15px 20px;
}
.cs_service_list a i {
  position: absolute;
  right: 20px;
  top: 50%;
  transform: translateY(-50%);
  color: #e9a132;
}
.cs_service_list a.active, .cs_service_list a:hover {
  background-color: #18191d;
  color: #fff;
}
.cs_service_list li:not(:last-child) {
  margin-bottom: 15px;
}

.cs_quick_contact_card {
  padding: 25px;
}
.cs_quick_contact_card .cs_quick_contact_card_in {
  border: 1px solid #6c6c6c;
  padding: 70px 20px;
}
.cs_quick_contact_card .cs_quick_contact_card_icon {
  margin-left: auto;
  margin-right: auto;
}

.cs_blockquote {
  border-left: 5px solid #e9a132;
}

/* Post Details */
.cs_sidebar_item {
  background-color: #f2f3f7;
  padding: 60px 40px;
}
.cs_sidebar_item:not(:last-child) {
  margin-bottom: 40px;
}
@media (max-width: 575px) {
  .cs_sidebar_item {
    padding: 30px 25px;
  }
}

.cs_sidebar_widget_title {
  font-size: 26px;
  margin-bottom: 20px;
  margin-top: -5px;
}

.widget_categories {
  margin-bottom: -5px;
}
.widget_categories ul {
  list-style: none;
  margin: 0;
  padding: 0;
  font-weight: 600;
  color: #18191d;
}
.widget_categories ul li a {
  display: inline-flex;
  padding-left: 30px;
  position: relative;
}
.widget_categories ul li a::before {
  content: "\f0a9";
  font-weight: 900;
  font-family: "Font Awesome 6 Free";
  position: absolute;
  left: 0;
  top: 0;
}
.widget_categories ul li:not(:last-child) {
  margin-bottom: 10px;
}

.tagcloud {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
}

.tag-cloud-link {
  background-color: #fff;
  font-size: 12px;
  line-height: 1.6em;
  padding: 7px 15px;
  box-shadow: 0px 4px 10px rgba(213, 213, 213, 0.25);
  border-radius: 5px;
}
.tag-cloud-link:hover {
  background-color: #e9a132;
  color: #fff;
}

.cs_sidebar_search {
  position: relative;
}
.cs_sidebar_search input {
  background: #ffffff;
  border: 1px solid #dfdfdf;
  width: 100%;
  height: 58px;
  padding: 5px 20px;
  outline: none;
}
.cs_sidebar_search .cs_sidebar_search_btn {
  position: absolute;
  right: 0;
  top: 0;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  border: none;
  background-color: transparent;
  color: #ababab;
  height: 58px;
  width: 58px;
  font-size: 18px;
  transition: all 0.4s ease;
}
.cs_sidebar_search .cs_sidebar_search_btn:hover {
  color: #e9a132;
}

.cs_recent_posts {
  list-style: none;
  padding: 0;
  margin: 0;
}
.cs_recent_posts li:not(:last-child) {
  margin-bottom: 20px;
}

.cs_recent_post {
  display: flex;
  align-items: center;
  padding: 10px;
  margin: -10px;
  transition: all 0.4s ease;
  border-radius: 10px;
}
.cs_recent_post:hover {
  background-color: #fff;
}
.cs_recent_post .cs_recent_post-thumb {
  flex: none;
  width: 100px;
  height: 93px;
  border-radius: 10px;
  overflow: hidden;
  margin-right: 15px;
}
.cs_recent_post .cs_recent_post-title {
  font-size: 16px;
  font-weight: 400;
  line-height: 1.5em;
  margin: 0;
}

.cs_recent_post-date {
  font-size: 12px;
  line-height: 1.6em;
  margin-bottom: 5px;
}
.cs_recent_post-date i {
  margin-right: 3px;
  color: #e9a132;
}

.cs_sidebar_project {
  display: block;
  position: relative;
  color: #fff;
}
.cs_sidebar_project span {
  position: absolute;
  font-weight: 600;
  bottom: 0;
  left: 0;
  z-index: 1;
  padding: 15px 25px;
}
.cs_sidebar_project img {
  transition: all 0.4s ease;
}
.cs_sidebar_project:hover {
  color: #e9a132;
}
.cs_sidebar_project:hover img {
  transform: scale(1.1);
}

.cs_sidebar_slider .cs_slider_prev,
.cs_sidebar_slider .cs_slider_next {
  top: 50%;
  transform: translateY(-50%);
}
.cs_sidebar_slider .cs_slider_prev {
  left: -15px;
}
