/* ================= BASE ================= */
.career-page {
  background: linear-gradient(180deg, #fafafa, #f3f3f3);
  padding: 0px 0;
}

/* ================= INTRO ================= */
.career-intro {
  max-width: 920px;
  margin: auto;
  padding: 80px 24px 60px;
}

.career-intro h2 {
  font-size: 34px;
  font-weight: 600;
  position: relative;
  display: inline-block;
}

.career-intro h2::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -10px;
  width: 60%;
  height: 3px;
  background: linear-gradient(90deg, #f5a400, transparent);
}

.career-intro p {
  margin-top: 22px;
  color: #555;
  line-height: 1.9;
}

.highlight {
  color: #f5a400;
  font-weight: 500;
}

/* ================= HEADER ================= */
.career-header {
  display: flex;
  align-items: center;
  gap: 14px;
  margin-bottom: 28px;
}

.career-header h3 {
  font-size: 26px;
}

.accent-line {
  height: 3px;
  width: 44px;
  background: #f5a400;
  animation: growLine 1s ease forwards;
}

@keyframes growLine {
  from { width: 0; }
  to { width: 44px; }
}

/* ================= FILTER BAR ================= */
.career-filters {
  display: grid;
  grid-template-columns: 1.6fr 1fr 1fr 1fr;
  gap: 16px;
  margin-bottom: 50px;
}

.career-filters input,
.career-filters select {
  padding: 14px 16px;
  border-radius: 8px;
  border: 1px solid #ddd;
  background: #fff;
  transition: all 0.35s cubic-bezier(.2,.8,.2,1);
}

.career-filters input:focus,
.career-filters select:focus {
  outline: none;
  border-color: #f5a400;
  box-shadow: 0 10px 25px rgba(245,164,0,0.25);
  transform: translateY(-2px);
}

/* ================= JOB GRID ================= */
.job-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(270px, 1fr));
  gap: 28px;
}

/* ================= JOB CARD ================= */
.job-card {
  background: #fff;
  padding: 28px;
  border-radius: 14px;
  box-shadow: 0 12px 30px rgba(0,0,0,0.08);
  transition: all 0.45s cubic-bezier(.2,.8,.2,1);
  position: relative;
  overflow: hidden;
}

.job-card::before {
  content: "";
  position: absolute;
  inset: 0;
  background: linear-gradient(
    120deg,
    transparent,
    rgba(245,164,0,0.15),
    transparent
  );
  opacity: 0;
  transition: opacity 0.5s ease;
}

.job-card:hover::before {
  opacity: 1;
}

.job-card:hover {
  transform: translateY(-10px) scale(1.02);
  box-shadow: 0 30px 70px rgba(0,0,0,0.18);
}

.job-card h4 {
  font-size: 18px;
  margin-bottom: 14px;
}

/* TAGS */
.job-tags {
  display: flex;
  flex-direction: column;
  gap: 6px;
  color: #666;
  font-size: 14px;
}

/* LINK */
.details-link {
  margin-top: 18px;
  display: inline-block;
  color: #f5a400;
  font-weight: 500;
  text-decoration: none;
  position: relative;
}

.details-link::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -3px;
  width: 0;
  height: 2px;
  background: #f5a400;
  transition: width 0.3s ease;
}

.details-link:hover::after {
  width: 100%;
}

/* ================= HEAVY ANIMATION ================= */
.reveal {
  opacity: 0;
  transform: translateY(40px) scale(0.97);
  transition:
    opacity 0.7s ease,
    transform 0.7s cubic-bezier(.2,.8,.2,1);
}

.reveal.active {
  opacity: 1;
  transform: translateY(0) scale(1);
}

/* Stagger effect */
.job-card.reveal.active {
  animation: cardEnter 0.7s cubic-bezier(.2,.8,.2,1) both;
}

@keyframes cardEnter {
  from {
    opacity: 0;
    transform: translateY(50px) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

/* EMPTY */
.no-result {
  color: #999;
  font-style: italic;
  padding: 20px;
}

/* ================= RESPONSIVE ================= */
@media (max-width: 900px) {
  .career-filters {
    grid-template-columns: 1fr;
  }
}
/* OPEN POSITIONS SECTION */
.career-jobs {
  width: 100%;
  background: #f9f9f9;
}

/* THIS IS THE KEY FIX */
.career-jobs-inner {
  max-width: 1200px;      /* controls content width */
  margin: 0 auto;        /* centers it */
  padding: 60px 20px;    /* small side padding, no gap */
  box-sizing: border-box;
}

/* HEADER ALIGNMENT */
.career-header {
  display: flex;
  align-items: center;
  gap: 14px;
  margin-bottom: 24px;
}

/* FILTER BAR */
.career-filters {
  width: 100%;
  display: grid;
  grid-template-columns: 1.6fr 1fr 1fr 1fr;
  gap: 16px;
  margin-bottom: 40px;
}

/* GRID SHOULD FILL CONTAINER */
.job-grid {
  width: 100%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 28px;
}

/* REMOVE ANY ACCIDENTAL MARGINS */
.job-card,
.career-header,
.career-filters {
  margin-left: 0;
  margin-right: 0;
}

/* MOBILE */
@media (max-width: 900px) {
  .career-filters {
    grid-template-columns: 1fr;
  }
}
/* ================= CAREER BANNER ================= */

.career-banner {
  position: relative;
  height: 50vh;
  width: 100%;
  display: flex;
  align-items: center;
  overflow: hidden;
  background: #0b0b0b;
}

/* BACKGROUND LAYER */
.career-banner-bg {
  position: absolute;
  inset: 0;
  z-index: 1;

  background-size: cover;
  background-position: center;

  transform: scale(1.15);
  animation: bannerZoom 8s cubic-bezier(0.2, 0.8, 0.2, 1) forwards;

  will-change: transform;
}

/* DARK OVERLAY (extra depth) */
.career-banner::after {
  content: "";
  position: absolute;
  inset: 0;
  z-index: 2;
  background: radial-gradient(
    circle at 30% 40%,
    rgba(0, 0, 0, 0.3),
    rgba(0, 0, 0, 0.85)
  );
}

/* CONTENT */
.career-banner-inner {
  position: relative;
  z-index: 3;
  max-width: 960px;
  padding: 80px 24px 0;
  color: #fff;
}

/* BADGE */
.career-badge {
  display: inline-block;
  padding: 8px 18px;
  border-radius: 30px;
  font-size: 12px;
  letter-spacing: 3px;
  font-weight: 600;
  color: #f5a400;
  background: rgba(245, 164, 0, 0.18);
  backdrop-filter: blur(6px);
  margin-bottom: 28px;
}

/* TITLE */
.career-title {
  font-size: 68px;
  font-weight: 700;
  line-height: 1.12;
  margin-bottom: 26px;

  background: linear-gradient(180deg, #ffffff, #dcdcdc);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/* SUBTITLE */
.career-subtitle {
  font-size: 19px;
  line-height: 1.9;
  color: #d0d0d0;
  max-width: 680px;
}

/* ACCENT LINE */
.career-banner-line {
  width: 90px;
  height: 3px;
  background: linear-gradient(90deg, #f5a400, transparent);
  margin-top: 40px;
  animation: growLine 1.4s cubic-bezier(0.2, 0.8, 0.2, 1) forwards;
}

/* ================= ANIMATIONS ================= */

@keyframes bannerZoom {
  from {
    transform: scale(1.15);
  }
  to {
    transform: scale(1);
  }
}

@keyframes growLine {
  from {
    width: 0;
  }
  to {
    width: 90px;
  }
}

/* REVEAL OVERRIDE */
.career-banner .reveal {
  opacity: 0;
  transform: translateY(70px);
  transition: all 1.1s cubic-bezier(0.2, 0.8, 0.2, 1);
}

.career-banner .reveal.active {
  opacity: 1;
  transform: translateY(0);
}

/* ACCESSIBILITY */
@media (prefers-reduced-motion: reduce) {
  .career-banner-bg,
  .career-banner-line {
    animation: none;
    transform: none;
  }
}

/* RESPONSIVE */
@media (max-width: 1024px) {
  .career-title {
    font-size: 52px;
  }
}

@media (max-width: 768px) {
  .career-banner {
    height: 75vh;
  }

  .career-title {
    font-size: 40px;
  }

  .career-subtitle {
    font-size: 16px;
  }
}
