.chairman {
  position: relative;
  min-height: 100vh;
  background: #fff;
  overflow: hidden;

  transform: translateY(140px);
  opacity: 0;

  transition:
    transform 1.2s cubic-bezier(0.22, 1, 0.36, 1),
    opacity 0.8s ease;
}

/* TOP DARK FADE MASK */
.chairman::before {
  content: "";
  position: absolute;
  inset: 0;
  background: linear-gradient(
    to bottom,
    rgba(0, 0, 0, 0.85) 0%,
    rgba(0, 0, 0, 0.35) 35%,
    transparent 70%
  );
  mix-blend-mode: multiply;

  transform: translateY(0);
  opacity: 1;

  transition:
    transform 1.3s cubic-bezier(0.22, 1, 0.36, 1),
    opacity 1.1s ease;
  pointer-events: none;
}

.chairman.is-visible {
  transform: translateY(0);
  opacity: 1;
}

/* MASK PEELS AWAY */
.chairman.is-visible::before {
  transform: translateY(-120px);
  opacity: 0;
}

/* CONTENT REVEAL – SLIGHTLY LATE */
.chairman-inner {
  position: relative;
  z-index: 2;
  max-width: 900px;
  margin: auto;
  padding: 140px 24px;

  transform: translateY(40px);
  opacity: 0;

  transition:
    transform 0.9s ease 0.35s,
    opacity 0.9s ease 0.35s;
}

.chairman.is-visible .chairman-inner {
  transform: translateY(0);
  opacity: 1;
}
