import { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const ChairmanMessage = () => {
  const sectionRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      gsap.from(".chairman-image", {
        y: 120,
        opacity: 0,
        scale: 0.95,
        scrollTrigger: {
          trigger: sectionRef.current,
          start: "top 80%",
          end: "top 40%",
          scrub: true,
        },
      });

      gsap.from(".chairman-content", {
        y: 80,
        opacity: 0,
        scrollTrigger: {
          trigger: sectionRef.current,
          start: "top 75%",
          end: "top 35%",
          scrub: true,
        },
      });
    }, sectionRef);

    return () => ctx.revert();
  }, []);

  return (
    <section ref={sectionRef} className="chairman-section">
      <div className="bg-dots" />
      <div className="bg-float-dots" />

      <div className="container chairman-wrapper">
        <div className="chairman-image">
          <img src="/assets/img/ajith-ns-3-scaled.webp" alt="Ajith NS" />
        </div>

        <div className="chairman-content">
          <h2>
            As the Chairman of <span>UGI</span>, I welcome you to our website
          </h2>

          <p className="chairman-text">
            I believe that great things in business are achieved through hard
            work as well as teamwork...
          </p>

          <div className="chairman-sign">
            <span>Ajith NS</span>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ChairmanMessage;
