commits.content:
.csr-banner {
  height: 380px;
  background-size: cover;
  background-position: center;
  position: relative;
}

.csr-banner-overlay {
  position: absolute;
  inset: 0;
  background: rgba(0,0,0,0.45);
}

/* spacing */
.csr-intro {
  margin-top: 100px;
}

.csr-intro-grid {
  display: grid;
  grid-template-columns: 1.1fr 0.9fr;
  gap: 80px;
  align-items: center;
}

.csr-text h2 {
  font-size: 42px;
  margin: 15px 0;
}

.csr-tag {
  color: #c9a24d;
  font-weight: 600;
  letter-spacing: 2px;
  font-size: 13px;
}

.csr-image img {
  width: 100%;
  border-radius: 18px;
}

/* initiatives */
.csr-initiatives {
  margin: 120px auto;
  display: grid;
  gap: 60px;
}

.csr-item {
  display: grid;
  grid-template-columns: 80px 1fr;
  gap: 30px;
  align-items: flex-start;
}

.csr-icon {
  width: 64px;
  height: 64px;
  border-radius: 16px;
  background: linear-gradient(135deg,#f6cf7a,#ffca58);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 26px;
  box-shadow: 0 20px 60px rgba(201,162,77,0.5);
}

.csr-content h3 {
  margin-bottom: 10px;
}

/* animation */
.reveal {
  opacity: 0;
  transform: translateY(40px);
  transition: all 0.8s ease;
}

.reveal.left {
  transform: translateX(-40px);
}

.reveal.right {
  transform: translateX(40px);
}

.reveal.active {
  opacity: 1;
  transform: translate(0);
}
.csr-banner {
 margin-top: 137px;
  height: 380px;
  margin-bottom: 80px; /* gap between banner and intro */
  background-size: cover;
  background-position: center;
  position: relative;
}
/* GRID */
.csr-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 32px;
  margin-top: 60px;
}

/* CARD */
.csr-card {
  display: flex;
  gap: 24px;
  padding: 32px;
  background: #ffffff;
  border-radius: 20px;
  box-shadow: 0 20px 60px rgba(0,0,0,0.06);
  border-left: 4px solid #e6b65c;
  transition: transform 0.35s ease, box-shadow 0.35s ease;
}

.csr-card:hover {
  transform: translateY(-6px);
  box-shadow: 0 30px 80px rgba(0,0,0,0.12);
}

/* ICON */
.csr-icon-wrap {
  flex-shrink: 0;
  width: 64px;
  height: 64px;
  border-radius: 16px;
  background: radial-gradient(circle at top left, #ffd77a, #e6b65c);
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 10px 30px rgba(230,182,92,0.45);
}

.csr-icon {
  font-size: 28px;
}

/* CONTENT */
.csr-content h3 {
  font-size: 20px;
  margin-bottom: 12px;
}

.csr-content p {
  font-size: 15px;
  line-height: 1.7;
  color: #555;
  margin-bottom: 10px;
}

/* REVEAL ANIMATION */
.reveal {
  opacity: 0;
  transform: translateY(40px);
  transition: all 0.8s ease;
}

.reveal.active {
  opacity: 1;
  transform: translateY(0);
}
/* ================= ACTIVITIES CTA ================= */

.csr-activities {
  background: linear-gradient(135deg, #0b0b0b, #111);
  padding: 120px 0;
  overflow: hidden;
}

.csr-activities-inner {
  display: grid;
  grid-template-columns: 1.1fr 0.9fr;
  align-items: center;
  gap: 80px;
}

/* TEXT */
.csr-activities-content h2 {
  font-size: 44px;
  font-weight: 600;
  margin: 14px 0 20px;
  color: #fff;
}

.csr-activities-content p {
  max-width: 520px;
  color: #cfcfcf;
  line-height: 1.7;
  margin-bottom: 32px;
}

/* BUTTON */
.csr-btn {
  display: inline-block;
  padding: 14px 34px;
  border-radius: 50px;
  background: linear-gradient(90deg, #f6c453, #ffdd88);
  color: #111;
  font-weight: 600;
  text-decoration: none;
  transition: all 0.35s ease;
  box-shadow: 0 10px 30px rgba(246, 196, 83, 0.25);
}

.csr-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 20px 40px rgba(246, 196, 83, 0.45);
}

/* IMAGES */
.csr-activities-images {
  position: relative;
  height: 420px;
}

.csr-activities-images .img {
  position: absolute;
  width: 280px;
  border-radius: 16px;
  box-shadow: 0 30px 60px rgba(0, 0, 0, 0.6);
  opacity: 0;
  transform: translateY(60px) scale(0.95);
  transition: all 0.9s cubic-bezier(0.2, 0.8, 0.2, 1);
}

.img-left {
  left: 0;
  top: 40px;
}

.img-right {
  right: 0;
  bottom: 0;
}

/* ACTIVE ANIMATION */
.reveal.active .csr-activities-images .img-left {
  opacity: 1;
  transform: translateY(0) scale(1);
  transition-delay: 0.2s;
}

.reveal.active .csr-activities-images .img-right {
  opacity: 1;
  transform: translateY(0) scale(1);
  transition-delay: 0.4s;
}

/* ZOOM REVEAL */
.reveal.zoom {
  opacity: 0;
  transform: scale(0.95);
  transition: all 0.8s ease;
}

.reveal.zoom.active {
  opacity: 1;
  transform: scale(1);
}

/* RESPONSIVE */
@media (max-width: 992px) {
  .csr-activities-inner {
    grid-template-columns: 1fr;
    text-align: center;
  }

  .csr-activities-images {
    height: 360px;
  }

  .csr-activities-images .img {
    position: relative;
    margin: 0 auto 20px;
  }
}
/* ================= HOVER INTERACTION ================= */

/* Smooth baseline */
.csr-activities-images .img {
  will-change: transform;
  transition: 
    transform 0.6s cubic-bezier(0.2, 0.8, 0.2, 1),
    box-shadow 0.6s ease;
}

/* Container hover */
.csr-activities-images:hover .img-left {
  transform: translateY(-12px) scale(1.08) rotate(-1deg);
  box-shadow: 0 40px 80px rgba(0, 0, 0, 0.75);
}

.csr-activities-images:hover .img-right {
  transform: translateY(-18px) scale(1.1) rotate(1deg);
  box-shadow: 0 50px 90px rgba(0, 0, 0, 0.85);
}

/* Optional: slight parallax feel */
.csr-activities-images:hover {
  transform: translateY(-4px);
}
