/* ================= DETAIL HEADER ================= */
.event-detail-header {
  position: relative;
  min-height: 600px;
  display: flex;
  align-items: center;
  background-size: cover;
  background-position: center;
  margin-top: 137px;
  overflow: hidden;
}

.detail-header-overlay {
  position: absolute;
  inset: 0;
  background: linear-gradient(135deg, rgba(0,0,0,0.85), rgba(0,0,0,0.6));
  backdrop-filter: blur(2px);
}

.detail-header-content {
  position: relative;
  z-index: 2;
  color: #fff;
  animation: fadeInUp 0.8s ease;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Breadcrumb */
.detail-breadcrumb {
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 24px;
  font-size: 14px;
  font-weight: 600;
  animation: fadeInUp 0.8s ease 0.1s both;
}

.detail-breadcrumb a {
  color: rgba(255, 255, 255, 0.8);
  text-decoration: none;
  transition: color 0.3s ease;
}

.detail-breadcrumb a:hover {
  color: #c9a24d;
}

.breadcrumb-sep {
  color: rgba(255, 255, 255, 0.4);
}

.detail-breadcrumb span:last-child {
  color: #fff;
}

/* Category Badge */
.detail-category {
  display: inline-block;
  padding: 10px 24px;
  border-radius: 50px;
  font-size: 12px;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  margin-bottom: 28px;
  box-shadow: 0 8px 30px rgba(0, 0, 0, 0.3);
  animation: fadeInUp 0.8s ease 0.2s both;
}

/* Title */
.detail-header-content h1 {
  font-size: 64px;
  font-weight: 800;
  line-height: 1.1;
  margin: 0 0 32px;
  max-width: 900px;
  text-shadow: 0 4px 20px rgba(0, 0, 0, 0.5);
  animation: fadeInUp 0.8s ease 0.3s both;
}

/* Meta Info */
.detail-meta {
  display: flex;
  flex-wrap: wrap;
  gap: 32px;
  animation: fadeInUp 0.8s ease 0.4s both;
}

.meta-item {
  display: flex;
  align-items: center;
  gap: 10px;
  color: rgba(255, 255, 255, 0.9);
  font-size: 15px;
  font-weight: 600;
}

.meta-item svg {
  stroke: #c9a24d;
  flex-shrink: 0;
}

/* ================= DETAIL CONTENT ================= */
.event-detail-content {
  padding: 100px 0;
  background: linear-gradient(180deg, #fafafa 0%, #ffffff 100%);
}

.detail-layout {
  display: grid;
  grid-template-columns: 1fr 380px;
  gap: 60px;
  align-items: start;
}

/* ================= MAIN CONTENT ================= */
.detail-main {
  animation: slideInLeft 0.8s ease;
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.detail-section {
  margin-bottom: 60px;
}

.section-title {
  font-size: 32px;
  font-weight: 800;
  color: #1a1a1a;
  margin: 0 0 32px;
  padding-bottom: 20px;
  border-bottom: 3px solid #f0f0f0;
  position: relative;
}

.section-title::before {
  content: '';
  position: absolute;
  bottom: -3px;
  left: 0;
  width: 80px;
  height: 3px;
  background: linear-gradient(90deg, #c9a24d, #f6cf7a);
}

.detail-paragraph {
  font-size: 17px;
  line-height: 1.9;
  color: #555;
  margin-bottom: 24px;
}

.detail-paragraph:last-of-type {
  margin-bottom: 0;
}

/* ================= DETAIL GALLERY ================= */
.detail-gallery {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 20px;
}

.gallery-image {
  position: relative;
  height: 280px;
  background-size: cover;
  background-position: center;
  border-radius: 16px;
  overflow: hidden;
  cursor: pointer;
  transition: all 0.4s ease;
}

.gallery-image:hover {
  transform: scale(1.03);
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.2);
}

.gallery-image:first-child {
  grid-column: 1 / -1;
  height: 400px;
}

.image-overlay {
  position: absolute;
  inset: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: opacity 0.4s ease;
}

.gallery-image:hover .image-overlay {
  opacity: 1;
}

.zoom-icon {
  width: 60px;
  height: 60px;
  background: rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
  border: 2px solid rgba(255, 255, 255, 0.4);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transform: scale(0.8);
  transition: transform 0.4s ease;
}

.gallery-image:hover .zoom-icon {
  transform: scale(1);
}

/* ================= SIDEBAR ================= */
.detail-sidebar {
  position: sticky;
  top: 160px;
  animation: slideInRight 0.8s ease;
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.sidebar-card {
  background: #fff;
  border-radius: 20px;
  padding: 32px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.08);
  margin-bottom: 24px;
}

.card-title {
  font-size: 20px;
  font-weight: 700;
  color: #1a1a1a;
  margin: 0 0 24px;
  padding-bottom: 16px;
  border-bottom: 2px solid #f0f0f0;
}

/* Info List */
.info-list {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.info-item {
  display: flex;
  gap: 16px;
  align-items: flex-start;
}

.info-icon {
  width: 44px;
  height: 44px;
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.15);
}

.info-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.info-label {
  font-size: 12px;
  font-weight: 600;
  color: #999;
  text-transform: uppercase;
  letter-spacing: 1px;
}

.info-value {
  font-size: 15px;
  font-weight: 600;
  color: #333;
  line-height: 1.5;
}

/* Guests List */
.guests-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 14px;
}

.guest-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  background: rgba(201, 162, 77, 0.05);
  border-radius: 12px;
  transition: all 0.3s ease;
}

.guest-item:hover {
  background: rgba(201, 162, 77, 0.1);
  transform: translateX(4px);
}

.guest-icon {
  color: #c9a24d;
  font-size: 12px;
}

.guest-name {
  font-size: 15px;
  font-weight: 600;
  color: #333;
}

/* Social Share */
.social-share {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 12px;
}

.share-btn {
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 12px;
  transition: all 0.3s ease;
  color: #fff;
}

.share-btn.facebook {
  background: #1877f2;
}

.share-btn.twitter {
  background: #1da1f2;
}

.share-btn.linkedin {
  background: #0077b5;
}

.share-btn.whatsapp {
  background: #25d366;
}

.share-btn:hover {
  transform: translateY(-4px);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
}

/* Back Button */
.back-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  width: 100%;
  padding: 16px;
  background: linear-gradient(135deg, #1a1a1a, #2a2a2a);
  color: #fff;
  border-radius: 12px;
  font-size: 15px;
  font-weight: 700;
  text-decoration: none;
  text-transform: uppercase;
  letter-spacing: 1px;
  transition: all 0.3s ease;
}

.back-btn:hover {
  background: linear-gradient(135deg, #c9a24d, #f6cf7a);
  transform: translateY(-2px);
  box-shadow: 0 8px 24px rgba(201, 162, 77, 0.3);
  color: #fff;
}

.back-btn svg {
  transition: transform 0.3s ease;
}

.back-btn:hover svg {
  transform: translateX(-4px);
}

/* ================= RESPONSIVE ================= */
@media (max-width: 1200px) {
  .detail-layout {
    grid-template-columns: 1fr 320px;
    gap: 40px;
  }
}

@media (max-width: 992px) {
  .event-detail-header {
    min-height: 500px;
    margin-top: 80px;
  }

  .detail-header-content h1 {
    font-size: 48px;
  }

  .detail-layout {
    grid-template-columns: 1fr;
    gap: 60px;
  }

  .detail-sidebar {
    position: static;
  }

  .detail-gallery {
    grid-template-columns: 1fr;
  }

  .gallery-image:first-child {
    grid-column: 1;
    height: 350px;
  }
}

@media (max-width: 768px) {
  .event-detail-header {
    min-height: 450px;
  }

  .detail-header-content h1 {
    font-size: 36px;
  }

  .detail-meta {
    flex-direction: column;
    gap: 16px;
  }

  .event-detail-content {
    padding: 60px 0;
  }

  .section-title {
    font-size: 26px;
  }

  .detail-paragraph {
    font-size: 16px;
  }

  .sidebar-card {
    padding: 24px;
  }

  .gallery-image,
  .gallery-image:first-child {
    height: 250px;
  }
}

@media (max-width: 480px) {
  .detail-header-content h1 {
    font-size: 28px;
  }

  .section-title {
    font-size: 22px;
  }

  .social-share {
    grid-template-columns: repeat(4, 1fr);
    gap: 8px;
  }

  .detail-breadcrumb {
    font-size: 12px;
    gap: 8px;
  }

  .meta-item {
    font-size: 14px;
  }
}
