/* ================= GLOBAL EVENT GALLERY ================= */
const EVENT_GALLERY = PREVIOUS_EVENTS.flatMap(event => event.gallery);

/* ================= COMPONENT ================= */
const Events = () => {
  useEffect(() => {
    const items = document.querySelectorAll(".reveal");

    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add("show");
            observer.unobserve(entry.target);
          }
        });
      },
      { threshold: 0.25 }
    );

    items.forEach(el => observer.observe(el));
  }, []);

  return (
    <>
      {/* PAGE HEADER */}
      <section
        className="cs_page_header"
        style={{ backgroundImage: "url(/assets/img/img/banner/event_banner.webp)" }}
      >
        <div className="container">
          <h1>Events</h1>
        </div>
      </section>

      {/* EVENTS LIST */}
      <section className="events-section">
        <div className="container">
          <div className="section-heading reveal">
            <span>Memories That Matter</span>
            <h2>Our Previous Events</h2>
          </div>

          <div className="row">
            {PREVIOUS_EVENTS.map(event => (
              <div key={event.slug} className="col-lg-4 col-md-6">
                <div className="event-card reveal">
                  <div
                    className="event-thumb"
                    style={{
                      backgroundImage: `url(${event.thumbnail})`
                    }}
                  >
                    <div className="event-overlay">
                      <p>{event.shortDesc}</p>
                      <Link
                        to={`/events/${event.slug}`}
                        className="event-btn"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>

                  <div className="event-info">
                    <h3>{event.title}</h3>
                    <p>{event.date}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* EVENT GALLERY (RESTORED) */}
      <section className="events-gallery">
        <div className="container">
          <div className="section-heading reveal">
            <span>Captured Moments</span>
            <h2>Event Gallery</h2>
          </div>

          <div className="gallery-grid">
            {EVENT_GALLERY.map((img, i) => (
              <div
                key={i}
                className="gallery-item reveal"
                style={{ backgroundImage: `url(${img})` }}
              />
            ))}
          </div>
        </div>
      </section>
    </>
  );
};

export default Events;
