/* ================= SECTION ================= */
.initiatives-details-enhanced {
  padding: 140px 0;
  background: linear-gradient(180deg, #fafafa 0%, #ffffff 50%, #f5f5f5 100%);
  position: relative;
}

/* ================= HEADING ================= */
.details-heading {
  text-align: center;
  margin-bottom: 80px;
}

.details-badge {
  display: inline-block;
  padding: 10px 28px;
  background: rgba(201, 162, 77, 0.1);
  border: 2px solid rgba(201, 162, 77, 0.3);
  border-radius: 50px;
  color: #c9a24d;
  font-size: 12px;
  font-weight: 700;
  letter-spacing: 3px;
  text-transform: uppercase;
  margin-bottom: 20px;
  animation: fadeIn 0.6s ease;
}

.details-heading h2 {
  font-size: 52px;
  font-weight: 800;
  color: #1a1a1a;
  margin: 0 0 20px;
  line-height: 1.2;
  animation: slideUp 0.8s ease 0.2s both;
}

.heading-accent {
  width: 100px;
  height: 4px;
  background: linear-gradient(90deg, transparent, #c9a24d, transparent);
  margin: 0 auto;
  border-radius: 2px;
  animation: expandWidth 0.8s ease 0.4s both;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes expandWidth {
  from { width: 0; }
  to { width: 100px; }
}

/* ================= LAYOUT ================= */
.details-layout {
  display: grid;
  grid-template-columns: 360px 1fr;
  gap: 60px;
  align-items: start;
}

/* ================= SIDEBAR ================= */
.details-sidebar {
  position: sticky;
  top: 140px;
  background: #fff;
  border-radius: 24px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.08);
  overflow: hidden;
  animation: slideInLeft 0.8s ease;
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.sidebar-header {
  padding: 32px 32px 24px;
  background: linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%);
  position: relative;
  overflow: hidden;
}

.sidebar-header::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 100%;
  background: linear-gradient(135deg, rgba(201, 162, 77, 0.1), transparent);
  pointer-events: none;
}

.sidebar-header h3 {
  color: #fff;
  font-size: 20px;
  font-weight: 700;
  margin: 0 0 12px;
  position: relative;
  z-index: 2;
}

.header-line {
  height: 3px;
  background: linear-gradient(90deg, #c9a24d, transparent);
  border-radius: 2px;
  position: relative;
  z-index: 2;
}

/* ================= NAV ================= */
.sidebar-nav {
  padding: 8px;
}

.nav-item {
  width: 100%;
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 18px 20px;
  background: transparent;
  border: none;
  border-radius: 16px;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  margin-bottom: 4px;
  position: relative;
  overflow: hidden;
}

.nav-item::before {
  content: '';
  position: absolute;
  inset: 0;
  background: var(--item-gradient);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.nav-item:hover::before {
  opacity: 0.08;
}

.nav-item.active::before {
  opacity: 0.12;
}

.nav-item:hover {
  transform: translateX(6px);
}

.nav-item.active {
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
}

.nav-icon {
  font-size: 28px;
  flex-shrink: 0;
  position: relative;
  z-index: 2;
  filter: grayscale(60%);
  transition: all 0.3s ease;
}

.nav-item:hover .nav-icon,
.nav-item.active .nav-icon {
  filter: grayscale(0%);
  transform: scale(1.1);
}

.nav-label {
  flex: 1;
  text-align: left;
  color: #555;
  font-size: 15px;
  font-weight: 600;
  position: relative;
  z-index: 2;
  transition: color 0.3s ease;
}

.nav-item:hover .nav-label,
.nav-item.active .nav-label {
  color: #1a1a1a;
}

.nav-arrow {
  color: #999;
  opacity: 0;
  transform: translateX(-8px);
  transition: all 0.3s ease;
  position: relative;
  z-index: 2;
}

.nav-item:hover .nav-arrow,
.nav-item.active .nav-arrow {
  opacity: 1;
  transform: translateX(0);
  color: #c9a24d;
}

/* ================= CONTENT ================= */
.details-content {
  transition: all 0.4s ease;
}

.details-content.fade-in {
  opacity: 1;
  transform: translateY(0);
}

.details-content.fade-out {
  opacity: 0;
  transform: translateY(20px);
}

.content-wrapper {
  background: #fff;
  border-radius: 28px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.08);
  overflow: hidden;
  animation: slideInRight 0.8s ease;
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(40px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

/* Icon Header */
.content-icon-wrapper {
  padding: 60px 50px 40px;
  position: relative;
  background: linear-gradient(135deg, rgba(0, 0, 0, 0.02), transparent);
}

.icon-glow {
  position: absolute;
  top: 50%;
  left: 80px;
  width: 160px;
  height: 160px;
  background: var(--accent-gradient);
  border-radius: 50%;
  filter: blur(80px);
  opacity: 0.25;
  transform: translate(-50%, -50%);
  animation: pulse-glow 4s ease-in-out infinite;
}

@keyframes pulse-glow {
  0%, 100% { transform: translate(-50%, -50%) scale(1); opacity: 0.25; }
  50% { transform: translate(-50%, -50%) scale(1.2); opacity: 0.4; }
}

.content-icon {
  font-size: 80px;
  display: inline-block;
  position: relative;
  z-index: 2;
  animation: bounceIn 0.8s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

@keyframes bounceIn {
  0% { transform: scale(0); }
  50% { transform: scale(1.15); }
  100% { transform: scale(1); }
}

/* Text Content */
.content-text {
  padding: 0 50px 50px;
}

.content-text h3 {
  font-size: 32px;
  font-weight: 800;
  color: #1a1a1a;
  margin: 0 0 12px;
  animation: fadeInUp 0.6s ease 0.2s both;
}

.content-text h4 {
  font-size: 18px;
  font-weight: 600;
  background: var(--accent-gradient);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin: 0 0 28px;
  animation: fadeInUp 0.6s ease 0.3s both;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.content-text p {
  font-size: 16px;
  line-height: 1.9;
  color: #555;
  margin: 0;
  animation: fadeInUp 0.6s ease 0.4s both;
}

.content-line {
  width: 100%;
  height: 3px;
  background: var(--accent-gradient);
  opacity: 0.3;
  margin-top: 40px;
  border-radius: 2px;
  animation: expandLine 0.8s ease 0.6s both;
}

@keyframes expandLine {
  from { transform: scaleX(0); }
  to { transform: scaleX(1); }
}

/* Image */
.content-image {
  position: relative;
  padding: 0 50px 50px;
  animation: fadeInUp 0.8s ease 0.5s both;
}

.image-border {
  position: absolute;
  inset: 50px;
  border: 3px solid transparent;
  border-radius: 20px;
  background: var(--accent-gradient);
  -webkit-mask: linear-gradient(#fff 0 0) padding-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor;
  mask-composite: exclude;
  opacity: 0.3;
  pointer-events: none;
}

.content-image img {
  width: 100%;
  border-radius: 20px;
  display: block;
  transition: transform 0.5s ease;
}

.content-image:hover img {
  transform: scale(1.02);
}

/* ================= RESPONSIVE ================= */
@media (max-width: 1200px) {
  .details-layout {
    grid-template-columns: 300px 1fr;
    gap: 40px;
  }
}

@media (max-width: 992px) {
  .details-layout {
    grid-template-columns: 1fr;
    gap: 40px;
  }

  .details-sidebar {
    position: static;
  }

  .sidebar-nav {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 8px;
  }
}

@media (max-width: 768px) {
  .initiatives-details-enhanced {
    padding: 100px 0 80px;
  }

  .details-heading h2 {
    font-size: 38px;
  }

  .sidebar-nav {
    grid-template-columns: 1fr;
  }

  .content-icon-wrapper {
    padding: 40px 30px 30px;
  }

  .content-icon {
    font-size: 64px;
  }

  .content-text {
    padding: 0 30px 40px;
  }

  .content-text h3 {
    font-size: 26px;
  }

  .content-text h4 {
    font-size: 16px;
  }

  .content-text p {
    font-size: 15px;
  }

  .content-image {
    padding: 0 30px 40px;
  }
}

@media (max-width: 480px) {
  .details-heading h2 {
    font-size: 28px;
  }

  .nav-item {
    padding: 16px;
  }

  .nav-icon {
    font-size: 24px;
  }

  .nav-label {
    font-size: 14px;
  }
}
