.job-banner {
  height: 55vh;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #000;
}

.job-banner-bg {
  position: absolute;
  inset: 0;
  background: linear-gradient(120deg, #111, #000);
}

.job-banner h1 {
  position: relative;
  color: #fff;
  font-size: 56px;
}

.job-details-container {
  max-width: 1200px;
  margin: auto;
  padding: 80px 20px;
  display: grid;
  grid-template-columns: 1.3fr 0.7fr;
  gap: 60px;
}

.job-info h2 {
  margin-bottom: 14px;
}

.job-meta {
  list-style: none;
  padding: 0;
  margin-bottom: 24px;
}

.company-box {
  margin-bottom: 30px;
}

.company-box a {
  color: #f5a400;
}

.job-info ul {
  margin-bottom: 30px;
}

.share-box button {
  margin-top: 20px;
}

.apply-form {
  background: #111;
  padding: 30px;
  border-radius: 16px;
}

.apply-form input,
.apply-form textarea {
  width: 100%;
  margin-bottom: 14px;
  padding: 12px;
  background: #0b0b0b;
  border: none;
  color: #000000;
}

.apply-form button {
  width: 100%;
  padding: 14px;
  border-radius: 40px;
  background: linear-gradient(90deg, #f5a400, #ffcc55);
  border: none;
  font-weight: 600;
}

.apply-form button:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

@media (max-width: 900px) {
  .job-details-container {
    grid-template-columns: 1fr;
  }

  .job-banner h1 {
    font-size: 38px;
  }
}
img {
    border: 0;
    max-width: 20%;
    height: auto;
}

/* ================= APPLY FORM ================= */

.apply-form {
  background: #ffffff;
  padding: 40px;
  border-radius: 18px;
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.08);
}

.apply-form h3 {
  font-size: 22px;
  margin-bottom: 28px;
  color: #111;
}

/* FORM GROUP */
.form-group {
  display: flex;
  flex-direction: column;
  margin-bottom: 20px;
}

.form-group label {
  font-size: 14px;
  font-weight: 500;
  margin-bottom: 8px;
  color: #333;
}

.form-group input,
.form-group textarea {
  padding: 14px 14px;
  border-radius: 10px;
  border: 1px solid #dcdcdc;
  font-size: 14px;
  transition: all 0.25s ease;
  background: #fff;
}

/* FOCUS STATE */
.form-group input:focus,
.form-group textarea:focus {
  outline: none;
  border-color: #f5a400;
  box-shadow: 0 0 0 3px rgba(245, 164, 0, 0.15);
}

/* FILE GROUP */
.file-group small {
  font-size: 12px;
  color: #777;
  margin-top: 6px;
}

/* POLICY */
.policy-group {
  flex-direction: row;
  align-items: center;
  gap: 10px;
  font-size: 13px;
  color: #555;
}

/* BUTTON */
.apply-form button {
  margin-top: 10px;
  width: 100%;
  padding: 14px;
  border-radius: 50px;
  border: none;
  font-size: 15px;
  font-weight: 600;
  cursor: pointer;
  background: linear-gradient(90deg, #f5a400, #ffcc55);
  color: #111;
  transition: transform 0.25s ease, box-shadow 0.25s ease;
}

.apply-form button:hover:not(:disabled) {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(245, 164, 0, 0.35);
}

.apply-form button:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}
.apply-form {
  background: #ffffff;
  padding: 40px;
  border-radius: 18px;
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.08);

  width: 500px;           /* ✅ fixed width */
  margin-bottom: 201px;   /* ✅ exact bottom spacing */
}
/* POLICY ROW – FIXED ALIGNMENT */
.policy-row {
  display: flex;
  align-items: center;     /* ✅ aligns checkbox + text vertically */
  gap: 10px;               /* ✅ spacing between checkbox and text */
  margin: 18px 0 26px;
  font-size: 14px;
  color: #333;
}

/* checkbox itself */
.policy-row input[type="checkbox"] {
  width: 16px;
  height: 16px;
  margin: 0;               /* ❌ remove default browser margin */
  cursor: pointer;
}

/* text */
.policy-row label {
  margin: 0;
  line-height: 1.4;
  cursor: pointer;
}

/* required star */
.policy-row span {
  color: red;
}
