/* ================= SECTION ================= */
.initiatives-section-enhanced {
  position: relative;
  padding: 140px 0;
  background: #fafafa;
  overflow: hidden;
}

/* ================= PARALLAX BACKGROUND ================= */
.initiatives-bg {
  position: absolute;
  inset: 0;
  overflow: hidden;
  pointer-events: none;
}

.bg-gradient-orb {
  position: absolute;
  border-radius: 50%;
  filter: blur(150px);
  opacity: 0.12;
  transition: transform 0.5s ease-out;
}

.orb-purple {
  width: 700px;
  height: 700px;
  background: radial-gradient(circle, #667eea, transparent);
  top: -250px;
  right: -200px;
}

.orb-blue {
  width: 600px;
  height: 600px;
  background: radial-gradient(circle, #4facfe, transparent);
  bottom: -200px;
  left: -150px;
}

.orb-pink {
  width: 500px;
  height: 500px;
  background: radial-gradient(circle, #f093fb, transparent);
  top: 40%;
  left: 30%;
}

/* ================= HEADING ================= */
.initiatives-heading-enhanced {
  text-align: center;
  margin-bottom: 80px;
  position: relative;
  z-index: 2;
}

.heading-badge {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  padding: 12px 30px;
  background: rgba(102, 126, 234, 0.08);
  border: 1px solid rgba(102, 126, 234, 0.2);
  border-radius: 50px;
  color: #667eea;
  font-size: 13px;
  font-weight: 600;
  letter-spacing: 1px;
  margin-bottom: 24px;
  animation: fadeInDown 0.8s ease;
}

.badge-dot {
  width: 8px;
  height: 8px;
  background: #667eea;
  border-radius: 50%;
  animation: pulse-dot 2s ease-in-out infinite;
}

@keyframes pulse-dot {
  0%, 100% { transform: scale(1); opacity: 1; }
  50% { transform: scale(1.3); opacity: 0.7; }
}

@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.initiatives-heading-enhanced h2 {
  font-size: 58px;
  font-weight: 800;
  color: #1a1a1a;
  margin: 0 0 16px;
  line-height: 1.2;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 20px;
  animation: fadeInUp 0.8s ease 0.2s both;
}

.heading-icon {
  color: #c9a24d;
  font-size: 48px;
  animation: rotate-icon 4s linear infinite;
}

@keyframes rotate-icon {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.heading-subtitle {
  font-size: 18px;
  color: #666;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.6;
  animation: fadeInUp 0.8s ease 0.4s both;
}

/* ================= GRID ================= */
.initiatives-grid-enhanced {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(360px, 1fr));
  gap: 40px;
  position: relative;
  z-index: 2;
}

/* ================= CARD ================= */
.initiative-card-enhanced {
  position: relative;
  background: #fff;
  border-radius: 28px;
  overflow: hidden;
  opacity: 0;
  transform: translateY(60px) scale(0.95);
  transition: all 0.8s cubic-bezier(0.4, 0, 0.2, 1);
  transition-delay: var(--animation-delay);
}

.initiative-card-enhanced.is-visible {
  opacity: 1;
  transform: translateY(0) scale(1);
}

.initiative-card-enhanced:hover {
  transform: translateY(-12px) scale(1.02);
  box-shadow: 0 30px 80px rgba(0, 0, 0, 0.12);
}

/* Card Glow */
.card-glow {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 200px;
  background: var(--card-gradient);
  opacity: 0;
  filter: blur(40px);
  transition: opacity 0.5s ease;
  pointer-events: none;
}

.initiative-card-enhanced:hover .card-glow {
  opacity: 0.3;
}

/* Card Inner */
.card-inner {
  position: relative;
  padding: 40px;
  border: 2px solid rgba(0, 0, 0, 0.05);
  border-radius: 28px;
  background: #fff;
  transition: border-color 0.4s ease;
}

.initiative-card-enhanced:hover .card-inner {
  border-color: var(--card-color);
}

/* Logo Wrapper */
.card-logo-wrapper {
  position: relative;
  width: 100px;
  height: 100px;
  margin: 0 auto 32px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.logo-bg {
  position: absolute;
  inset: -10px;
  background: var(--card-gradient);
  border-radius: 24px;
  opacity: 0.1;
  transform: rotate(0deg);
  transition: all 0.5s ease;
}

.initiative-card-enhanced:hover .logo-bg {
  opacity: 0.2;
  transform: rotate(8deg) scale(1.1);
}

.card-logo {
  width: 100%;
  height: 100%;
  object-fit: contain;
  position: relative;
  z-index: 2;
  filter: grayscale(20%);
  transition: all 0.5s ease;
}

.initiative-card-enhanced:hover .card-logo {
  filter: grayscale(0%);
  transform: scale(1.1);
}

/* Card Content */
.card-content h3 {
  font-size: 24px;
  font-weight: 700;
  color: #1a1a1a;
  margin: 0 0 16px;
  transition: color 0.3s ease;
}

.initiative-card-enhanced:hover .card-content h3 {
  color: var(--card-color);
}

.card-content p {
  font-size: 15px;
  line-height: 1.8;
  color: #666;
  margin: 0;
}

/* Card Overlay */
.card-overlay {
  position: absolute;
  inset: 0;
  opacity: 0;
  transition: opacity 0.5s ease;
  pointer-events: none;
  border-radius: 28px;
  overflow: hidden;
}

.overlay-gradient {
  position: absolute;
  inset: 0;
  background: var(--card-gradient);
  opacity: 0.03;
}

.initiative-card-enhanced:hover .card-overlay {
  opacity: 1;
}

/* Corner Accent */
.card-corner {
  position: absolute;
  top: 0;
  right: 0;
  width: 80px;
  height: 80px;
  background: var(--card-gradient);
  opacity: 0;
  clip-path: polygon(100% 0, 100% 100%, 0 0);
  transition: opacity 0.5s ease;
}

.initiative-card-enhanced:hover .card-corner {
  opacity: 0.15;
}

/* ================= DECORATION ================= */
.initiatives-decoration {
  margin-top: 80px;
  position: relative;
  z-index: 2;
}

.deco-line {
  height: 4px;
  background: linear-gradient(90deg, transparent, #c9a24d, transparent);
  border-radius: 2px;
  transform-origin: center;
  animation: glow-line 3s ease-in-out infinite;
}

@keyframes glow-line {
  0%, 100% { box-shadow: 0 0 10px rgba(201, 162, 77, 0.3); }
  50% { box-shadow: 0 0 30px rgba(201, 162, 77, 0.6); }
}

/* ================= RESPONSIVE ================= */
@media (max-width: 1200px) {
  .initiatives-grid-enhanced {
    grid-template-columns: repeat(2, 1fr);
    gap: 32px;
  }
}

@media (max-width: 768px) {
  .initiatives-section-enhanced {
    padding: 100px 0 80px;
  }

  .initiatives-heading-enhanced h2 {
    font-size: 40px;
    flex-direction: column;
    gap: 12px;
  }

  .heading-subtitle {
    font-size: 16px;
  }

  .initiatives-grid-enhanced {
    grid-template-columns: 1fr;
    gap: 24px;
  }

  .card-inner {
    padding: 32px 24px;
  }

  .card-logo-wrapper {
    width: 80px;
    height: 80px;
    margin-bottom: 24px;
  }

  .card-content h3 {
    font-size: 20px;
  }

  .card-content p {
    font-size: 14px;
  }
}

@media (max-width: 480px) {
  .initiatives-heading-enhanced h2 {
    font-size: 32px;
  }

  .heading-icon {
    font-size: 36px;
  }
}
