+++ 
@@ -0,0 +1,1431 @@
+
+
+body {
+  margin: 0;
+  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen',
+    'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue',
+    sans-serif;
+  -webkit-font-smoothing: antialiased;
+  -moz-osx-font-smoothing: grayscale;
+}
+
+code {
+  font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New',
+    monospace;
+}
+
+
+/* ===============================
+   STICKY HEADER BASE
+================================ */
+
+.cs_sticky_header {
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100%;
+  z-index: 999;
+
+  /* glass background */
+  background: rgba(34, 36, 44, 0.85); /* #22242c */
+  backdrop-filter: blur(14px);
+  -webkit-backdrop-filter: blur(14px);
+
+  transition:
+    transform 0.35s ease-in-out,
+    background 0.3s ease,
+    backdrop-filter 0.3s ease;
+}
+
+/* ===============================
+   ACTIVE STICKY (SLIDE IN)
+================================ */
+
+.cs_sticky_active {
+  transform: translateY(0);
+  animation: slideDown 0.35s ease-in-out;
+}
+
+/* slide animation */
+@keyframes slideDown {
+  from {
+    transform: translateY(-100%);
+  }
+  to {
+    transform: translateY(0);
+  }
+}
+
+/* ===============================
+   HIDE HEADER ON SCROLL
+================================ */
+
+.cs_header_hide {
+  transform: translateY(-100%);
+}
+
+/* ===============================
+   TOP HEADER (OPTIONAL BLUR)
+================================ */
+
+.cs_top_header {
+  background: rgba(34, 36, 44, 0.9);
+  backdrop-filter: blur(12px);
+  -webkit-backdrop-filter: blur(12px);
+}
+
+/* ===============================
+   FALLBACK (NO BLUR SUPPORT)
+================================ */
+
+@supports not ((backdrop-filter: blur(1px))) {
+  .cs_sticky_header,
+  .cs_top_header {
+    background: #22242c;
+  }
+}
+
+
+
+/* chairman message section */
+/* ===============================
+   SECTION BASE
+================================ */
+.chairman-section {
+  position: relative;
+  background: #050505;
+  padding: 130px 0;
+  overflow: hidden;
+}
+
+/* GOLD AMBIENT LIGHT */
+.chairman-section::before {
+  content: "";
+  position: absolute;
+  inset: -20%;
+  background: radial-gradient(
+    circle at 20% 50%,
+    rgba(245, 185, 66, 0.16),
+    transparent 60%
+  );
+  animation: goldMove 18s linear infinite;
+  z-index: 0;
+}
+
+/* DARK VIGNETTE */
+.chairman-section::after {
+  content: "";
+  position: absolute;
+  inset: 0;
+ background: radial-gradient(ellipse at center, #000000 35%, rgba(0, 0, 0, 0.92) 80%);
+  z-index: 1;
+}
+
+/* ===============================
+   BACKGROUND ELEMENTS
+================================ */
+.bg-dots {
+  position: absolute;
+  inset: 0;
+  background-image: radial-gradient(
+    rgba(255, 255, 255, 0.05) 1px,
+    transparent 1px
+  );
+  background-size: 22px 22px;
+  opacity: 0.25;
+  z-index: 1;
+  pointer-events: none;
+}
+
+.bg-float-dots {
+  position: absolute;
+  inset: -20%;
+  background-image: radial-gradient(
+    rgba(245, 185, 66, 0.18) 1.5px,
+    transparent 2px
+  );
+  background-size: 60px 60px;
+  animation: floatDots 35s linear infinite;
+  opacity: 0.35;
+  z-index: 1;
+  pointer-events: none;
+}
+
+/* ===============================
+   LAYOUT
+================================ */
+.chairman-wrapper {
+  display: flex;
+  align-items: center;
+  gap: 90px;
+  position: relative;
+  z-index: 2;
+}
+
+/* ===============================
+   IMAGE
+================================ */
+.chairman-image {
+  flex: 0 0 45%;
+  display: flex;
+  justify-content: center;
+  opacity: 0;
+  transform: translateY(40px);
+  transition: all 1.2s ease;
+}
+
+.chairman-section.is-visible .chairman-image {
+  opacity: 1;
+  transform: translateY(0);
+}
+
+.chairman-image img {
+  max-width: 100%;
+  width: 100%;
+  border-radius: 6px;
+  filter:
+    drop-shadow(0 35px 65px rgba(0, 0, 0, 0.85))
+    brightness(1.04)
+    contrast(1.06);
+}
+
+/* ===============================
+   CONTENT
+================================ */
+.chairman-content {
+  flex: 0 0 55%;
+}
+
+/* HEADING */
+.chairman-content h2 {
+  font-size: 34px;
+  font-weight: 600;
+  line-height: 1.35;
+  color: #ffffff;
+  margin-bottom: 34px;
+  opacity: 0;
+  transform: translateY(30px);
+  transition: all 1s ease 0.2s;
+}
+
+.chairman-content h2 span {
+  color: #f5b942;
+}
+
+/* TEXT */
+.chairman-text {
+  font-size: 16px;
+  line-height: 1.9;
+  font-style: italic;
+  color: #d6d6d6;
+  margin-bottom: 45px;
+  opacity: 0;
+  transform: translateY(30px);
+  transition: all 1s ease 0.45s;
+}
+
+/* SIGNATURE */
+.chairman-sign {
+  opacity: 0;
+  transform: translateY(20px);
+  transition: all 1s ease 0.7s;
+}
+
+.chairman-sign span {
+  font-size: 26px;
+  color: #ffffff;
+}
+
+/* ACTIVATE */
+.chairman-section.is-visible h2,
+.chairman-section.is-visible .chairman-text,
+.chairman-section.is-visible .chairman-sign {
+  opacity: 1;
+  transform: translateY(0);
+}
+
+/* ===============================
+   ANIMATIONS
+================================ */
+@keyframes goldMove {
+  0% { transform: translateX(-15%); }
+  50% { transform: translateX(15%); }
+  100% { transform: translateX(-15%); }
+}
+
+@keyframes floatDots {
+  0% { transform: translate(0, 0); }
+  50% { transform: translate(80px, -40px); }
+  100% { transform: translate(0, 0); }
+}
+
+/* ===============================
+   RESPONSIVE
+================================ */
+@media (max-width: 992px) {
+  .chairman-wrapper {
+    flex-direction: column;
+    text-align: center;
+    gap: 50px;
+  }
+
+  .chairman-content h2 {
+    font-size: 26px;
+  }
+}
+
+
+/* initiatives SECTION */
+/* SECTION */
+
+/* =========================
+   SECTION â€“ WHITE THEME
+========================= */
+.initiatives-section {
+  background: #ffffff;
+  padding: 120px 0;
+  color: #111111;
+}
+
+/* =========================
+   HEADING
+========================= */
+.initiatives-heading {
+  text-align: center;
+  margin-bottom: 70px;
+}
+
+.initiatives-heading h4 {
+  font-size: 14px;
+  letter-spacing: 2px;
+  text-transform: uppercase;
+  color: #777;
+  margin-bottom: 12px;
+}
+
+.initiatives-heading h2 {
+  font-size: 42px;
+  font-weight: 600;
+  color: #111;
+  position: relative;
+}
+
+.initiatives-heading h2::after {
+  content: "";
+  position: absolute;
+  left: 50%;
+  bottom: -18px;
+  width: 120px;
+  height: 3px;
+  background: linear-gradient(
+    90deg,
+    transparent,
+    #f5b942,
+    transparent
+  );
+  transform: translateX(-50%);
+}
+
+.initiatives-icon {
+  color: #f5b942;
+  margin-right: 10px;
+}
+
+/* =========================
+   GRID
+========================= */
+.initiatives-grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
+  gap: 40px;
+}
+
+/* =========================
+   CARD â€“ BASE
+========================= */
+.initiative-card {
+  background: #ffffff;
+  padding: 50px 32px;
+  border-radius: 18px;
+  text-align: center;
+  position: relative;
+  overflow: hidden;
+
+  /* GOLD BORDER */
+  border: 2px solid rgba(245, 185, 66, 0.5);
+
+  /* SHADOW */
+  box-shadow:
+    0 20px 40px rgba(0, 0, 0, 0.08);
+
+  /* SCROLL ANIMATION START */
+  opacity: 0;
+  transform: translate3d(var(--direction), 60px, 0) scale(0.92);
+  filter: blur(6px);
+
+  transition:
+    opacity 0.7s ease var(--delay),
+    transform 0.9s cubic-bezier(0.22, 1, 0.36, 1) var(--delay),
+    filter 0.6s ease var(--delay),
+    box-shadow 0.4s ease;
+}
+
+/* =========================
+   ACTIVE (ON SCROLL)
+========================= */
+.initiative-card.is-active {
+  opacity: 1;
+  transform: translate3d(0, 0, 0) scale(1);
+  filter: blur(0);
+}
+
+/* =========================
+   GOLDEN GLOW BORDER (RUNNING)
+========================= */
+.initiative-card::before {
+  content: "";
+  position: absolute;
+  inset: -2px;
+  border-radius: 20px;
+  background: linear-gradient(
+    120deg,
+    transparent 20%,
+    rgba(245, 185, 66, 0.9),
+    transparent 80%
+  );
+  opacity: 0.6;
+  animation: goldGlow 3.5s linear infinite;
+  pointer-events: none;
+}
+
+@keyframes goldGlow {
+  0% {
+    transform: translateX(-100%);
+  }
+  100% {
+    transform: translateX(100%);
+  }
+}
+
+/* =========================
+   HOVER â€“ PREMIUM LIFT
+========================= */
+.initiative-card:hover {
+  transform: translateY(-10px) scale(1.03);
+  box-shadow:
+    0 30px 60px rgba(245, 185, 66, 0.25);
+}
+
+/* =========================
+   LOGO
+========================= */
+.initiative-logo {
+  height: 110px;
+  margin-bottom: 28px;
+  display: flex;
+  align-items: center;
+  justify-content: center;
+}
+
+.initiative-logo img {
