  from {
    opacity: 0;
    transform: translateY(15px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* IMAGE */
.established-image img {
  width: 100%;
  animation: imageZoom 0.6s ease;
}

@keyframes imageZoom {
  from {
    opacity: 0;
    transform: scale(1.04);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}


