}

/* LEFT */
.contact-form-area h2 {
  font-size: 34px;
  margin-bottom: 8px;
}

.contact-form-area .sub {
  color: #777;
  margin-bottom: 30px;
}

.field-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 16px;
}

.contact-form-area input,
.contact-form-area textarea {
  width: 100%;
  padding: 14px 18px;
  border-radius: 14px;
  border: none;
  background: #f4f6fa;
  font-size: 14px;
  margin-bottom: 16px;
}

.contact-form-area input:focus,
.contact-form-area textarea:focus {
  outline: none;
  box-shadow: 0 0 0 2px rgba(201,162,77,0.35);
}

.contact-form-area button {
  margin-top: 10px;
  padding: 12px 36px;
  border-radius: 20px;
  border: none;
  background: linear-gradient(
    135deg,
    #f6cf7a,
    #ffca58
  );
  color: #111;
  font-size: 13px;
  letter-spacing: 1px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.contact-form-area button:hover {
  transform: translateY(-2px);
  box-shadow: 0 14px 40px rgba(201,162,77,0.45);
}

/* RIGHT */
.contact-info-area {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

/* ICON BOX */
.icon-glow {
  width: 86px;
  height: 86px;
  border-radius: 22px;
  background: linear-gradient(135deg, #f6cf7a, #ffca58);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 34px;
  box-shadow:
    0 18px 50px rgba(201,162,77,0.45),
    inset 0 0 20px rgba(255,255,255,0.35);
}

/* MESSAGE FLOAT ANIMATION */
.message-anim {
  animation: floatSoft 4s ease-in-out infinite;
}

@keyframes floatSoft {
  0% { transform: translateY(0); }
  50% { transform: translateY(-8px); }
  100% { transform: translateY(0); }
}

/* CONTACT LIST */
.contact-list {
  list-style: none;
  padding: 0;
  margin: 0;
}

.contact-list li {
  display: flex;
  align-items: flex-start;
  gap: 14px;
  margin-bottom: 22px;
}

.contact-list .icon {
  font-size: 18px;
  line-height: 1;
  margin-top: 3px;
}

.contact-list small {
  display: block;
  font-size: 11px;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: #888;
  margin-bottom: 3px;
}

.contact-list strong {
  font-size: 15px;
  font-weight: 500;
  color: #111;
}

/* SOCIALS */
.socials {
  margin-top: 28px;
}

.socials i {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: #f4f6fa;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  margin-right: 10px;
  cursor: pointer;
  color: #444;
  transition: all 0.3s ease;
}

.socials i:hover {
  background: linear-gradient(135deg, #f6cf7a, #ffca58);
  color: #111;
  box-shadow: 0 10px 30px rgba(201,162,77,0.4);
  transform: translateY(-3px);
}


/* GLOWS */
.glow {
  position: absolute;
  border-radius: 50%;
  filter: blur(90px);
  opacity: 0.55;
}

.glow-gold {
  width: 300px;
  height: 300px;
  background: rgba(246,207,122,0.6);
  top: -140px;
  right: -140px;
}

.glow-soft {
  width: 220px;
  height: 220px;
  background: rgba(201,162,77,0.35);
  bottom: -120px;
  left: -120px;
}
/* CONTACT VISUAL IMAGE */
.contact-visual {
  position: relative;
  width: 100%;
  max-width: 360px;
  margin-bottom: 30px;
  animation: floatImage 6s ease-in-out infinite;
}

.contact-visual img {
  width: 100%;
  height: auto;
  display: block;
  filter: drop-shadow(0 30px 80px rgba(201,162,77,0.35));
}

/* GOLDEN GLOW BEHIND IMAGE */
.contact-visual::before {
  content: "";
  position: absolute;
  inset: -20px;
  background: radial-gradient(
    circle,
    rgba(255, 200, 90, 0.35),
    transparent 70%
  );
  filter: blur(40px);
  z-index: -1;
}

/* FLOATING MOTION */
@keyframes floatImage {
  0%   { transform: translateY(0) rotate(0.3deg); }
  50%  { transform: translateY(-14px) rotate(-0.3deg); }
  100% { transform: translateY(0) rotate(0.3deg); }
}
/* OFFICE BLOCK */
.office-block {
  margin-bottom: 28px;
}

.office-title {
  font-size: 15px;
  font-weight: 600;
  margin-bottom: 14px;
  color: #111;
  position: relative;
}

.office-title::after {
  content: "";
  display: block;
  width: 36px;
  height: 2px;
  background: linear-gradient(90deg, #f6cf7a, #ffca58);
  margin-top: 6px;
}
/* RIGHT AREA */
.contact-info-area {
  display: flex;
  flex-direction: column;
  align-items: center;
}

/* CENTER VISUAL */
.center-visual {
  margin-bottom: 40px;
}

.center-visual img {
  width: 260px;
  max-width: 100%;
  filter: drop-shadow(0 30px 80px rgba(201,162,77,0.45));
}

/* FLOAT ANIMATION */
.float-anim {
  animation: floatUpDown 6s ease-in-out infinite;
}

@keyframes floatUpDown {
  0% { transform: translateY(0); }
  50% { transform: translateY(-18px); }
  100% { transform: translateY(0); }
}

/* INFO GRID */
.contact-info-grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 36px;
  width: 100%;
  max-width: 520px;
}

/* OFFICE CARD */
.office-card {
  background: rgba(246 247 251);
  padding: 22px;
  border-radius: 16px;
  font-size: 14px;
  line-height: 1.6;
}

/* TITLE */
.office-title {
  font-size: 15px;
  font-weight: 600;
  margin-bottom: 12px;
  position: relative;
}

.office-title::after {
  content: "";
  width: 32px;
  height: 2px;
  background: linear-gradient(90deg, #f6cf7a, #ffca58);
  display: block;
  margin-top: 6px;
}

/* SOCIALS */
.center-socials {
  margin-top: 30px;
}

.socials i {
  margin: 0 10px;
  font-size: 16px;
  cursor: pointer;
  color: #444;
  transition: color 0.3s ease;
}

.socials i:hover {
  color: #c9a24d;
}

/* RESPONSIVE */
@media (max-width: 768px) {
  .contact-info-grid {
    grid-template-columns: 1fr;
  }
}
/* ================= MAP SECTION ================= */
.location-map-section {
  margin-top: 100px;
  padding: 0 0 80px;
  background: linear-gradient(
    to bottom,
    #ffffff 0%,
    #fffaf0 40%,
    #ffffff 100%
  );
}

.location-map-header {
  text-align: center;
  margin-bottom: 30px;
}

.location-map-header h2 {
  font-size: 32px;
  font-weight: 600;
}

.location-map-header p {
  color: #777;
  font-size: 14px;
}

/* Map container */
.location-map-wrapper {
  position: relative;
  width: 100%;
  height: 460px;
  border-radius: 26px;
  overflow: hidden;
  box-shadow:
    0 40px 120px rgba(0,0,0,0.08),
    0 0 80px rgba(255, 255, 255, 0.25);
}

/* Google iframe */
.location-map-wrapper iframe {
  width: 100%;
  height: 100%;
  border: none;
}

/* subtle gold glow */
.location-map-wrapper::after {
  content: "";
  position: absolute;
  inset: -20px;
  background: radial-gradient(
    circle at center,
    rgba(250, 250, 250, 0.35),
    transparent 70%
  );
  pointer-events: none;
}
/* ================= BANNER ================= */
.contact-banner {
  position: relative;
  height: 320px;
  background-size: cover;
  background-position: center;
  display: flex;
  align-items: center;
}

.contact-banner-overlay {
  position: absolute;
  inset: 0;
  background:
    linear-gradient(
      to right,
      rgba(0,0,0,0.65),
      rgba(0,0,0,0.35)
    ),
    radial-gradient(
      circle at center,
      rgba(201,162,77,0.35),
      transparent 70%
    );
}

.contact-banner-content {
  position: relative;
  z-index: 1;
  padding-left: 8%;
  color: #fff;
}

.contact-banner-content h1 {
  font-size: 48px;
  margin-bottom: 10px;
}

.contact-banner-content p {
  font-size: 16px;
  max-width: 520px;
  opacity: 0.9;
}
.contact-container {
  margin-top: -80px; /* pulls card slightly into banner */
  padding-bottom: 80px;
}
