import { useEffect } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

const HomePage = () => {
  useEffect(() => {
    ScrollTrigger.refresh();
  }, []);

  return (
    <>
      <Header />
      <Hero />
      <ChairmanMessage />
      <ServicesSection />
      <Established />
      <ScrollGallery />
      <Footer />
    </>
  );
};

export default HomePage;
